import React, { useState } from 'react';

const AddUser = ({onAddNewUser}) => {

  const [addNewUser, setAddNewUser] = useState({name: '', desc:'', age:'',isAgree: ''})

  const onAdd = () => {
    const newUser = {
      ...addNewUser,
      id: Date.now(),
    }
    onAddNewUser(newUser)
  }

  return (
    <form>
      <input type='text' placeholder='Имя' value={addNewUser.name} onChange={e => setAddNewUser({name: e.target.value})}/>
      <input type='text' placeholder='О себе' value={addNewUser.desc} onChange={e => setAddNewUser({desc: e.target.value})}/>
      <input type='text' placeholder='Возраст' value={addNewUser.age} onChange={e => setAddNewUser({age: e.target.value})}/>

      <label htmlFor="isAgree" onChange={e => setAddNewUser({isAgree: e.target.checked})}>Согласен</label>
      <input type="checkbox" id='isAgree' />
      <button type="button" onClick={onAdd}>Добавить</button>
    </form>

  )
}

export default AddUser
