import React, { useState } from "react"
import AddUser from "./components/AddUser"
import Header from "./components/Header"
import Users from "./components/Users"
import axios from 'axios'
  // axios.get(url).then((response) => {
  //   setState({users: response.data.data})
  // })
const url = 'https://reqres.in/api/users?page=1'

const App = () => {

const [users, setUsers] = useState([{id: 1, name: 'Sam', desc: 'Hi! im Sam!', age: 40, isAgree: true}])

  
  const onAddNewUser = (newUser) => {
    setUsers([...users, newUser])
 }

  const removeUser = (user) => {
    setUsers(users.filter(u => u.id !== user.id))
  }



  return (
    <div>
      <Header />
      <main>
        <Users users={users} removeUser={removeUser} />
      </main>
      <aside>
        <AddUser onAddNewUser={onAddNewUser} />
      </aside>
    </div>
  )
}


export default App
