import React, { useEffect, useMemo, useState } from "react"
import AddUser from "./components/AddUser"
import Header from "./components/Header"
import UsersList from "./components/UsersList"
import axios from 'axios'
import SortUsers from "./components/SortUsers"
import FilterUsers from "./components/FilterUsers"
import TestAdd from './TestAdd'

const url = "https://jsonplaceholder.typicode.com/users"

const App = () => {

const [users, setUsers] = useState([{id: '', email:'', name: '', username: '', age: '', isAgree: ''}])

const [filter, setFilter] = useState({sort:'', search:''})


useEffect(() =>{ fetch(url).then(response => response.json().then(json => setUsers(json)))},[])

 
  const onAddNewUser = (newUser) => {
    setUsers([...users, newUser])
 }

  const removeUser = (user) => {
    setUsers(users.filter(u => u.id !== user.id))
  }

  const EditUser = (user) => {
    console.log(user)
    users[user.id - 1] = user
    setUsers([...users])
  }

  const sortUsers = (sortValue) => {
    setFilter({...filter, sort: sortValue})
  }

  const sortedUsers =  useMemo(() => {
    console.log("Отработала SortedUsers")

    if(filter.sort){
      return [...users].sort((a,b) => a[filter.sort].localeCompare(b[filter.sort]))
    }
    return users

  }, [filter.sort, users])


  const searchAndSortUsers = useMemo(() => {
    console.log("Отработала searchAndSortUsers")
    return sortedUsers.filter(s => s.name.toLowerCase().includes(filter.search))
  }, [filter.search, sortedUsers])



const add = (user) => {
 setUsers([...users, user])
}
const onRemoveUser = (user) => {
  users.map(u => setUsers(u.filter(u => u.id !== user.id)))
  
}

// type email in input

  return (
    <div>
      <h1>{}</h1>
      <TestAdd onRemoveUser={onRemoveUser} users={users} add={add} />
      <Header />
      <FilterUsers filter={filter} setFilter={setFilter} sortUsers={sortUsers} />
      <main>
        <UsersList users={searchAndSortUsers} removeUser={removeUser} EditUser={EditUser} />
      </main>
      <aside>
        <AddUser users={users} onAddNewUser={onAddNewUser} />
      </aside>
    </div>
  )
}


export default App
