import React, { setState } from 'react';

const AddUser = () => {
const  state = {
    name:'',
    descriptions: '',
    age: 0,

  }
  return (
    <form>
      <input placeholder="Имя" onChange={(e) => setState({name: e.target.value})}/>
      <input placeholder="Описание" onChange={(e) => setState({descriptions: e.target.value})} />
      <input placeholder="Возраст" onChange={(e) => setState({age: e.target.value})} />
      <label htmlFor="isAgree" onChange={(e) => setState({})}>Согласен</label>
      <input type="checkbox" id='isAgree' />
      <button type="button">Добавить</button>
    </form>

  )
}

export default AddUser
