import React, { useEffect, useState } from "react"
import AddUser from "./components/AddUser"
import Header from "./components/Header"
import UsersList from "./components/UsersList"
import axios from 'axios'
import SortUsers from "./components/SortUsers"

const url = "https://jsonplaceholder.typicode.com/users"

const App = () => {

const [users, setUsers] = useState([{id: 1, email:'sql@gmail.com', first_name: 'Sam', last_name: 'Hi! im Sam!', age: 40, isAgree: true}])

useEffect(() =>{ fetch(url).then(response => response.json().then(json => setUsers(json)))},[users])

// axios.get(url).then((response) => {
//   setUsers({...users, users: response.data})
// })  
  const onAddNewUser = (newUser) => {
    setUsers([...users, newUser])
 }

  const removeUser = (user) => {
    setUsers(users.filter(u => u.id !== user.id))
  }

  const [selectSort, setSelectSort] = useState('')

  const sortUsers = (sort) => {
    setSelectSort(sort)
    setUsers([...users].sort((a,b) => a[sort].localeCompare(b[sort])))
  }

  const EditUser = (user) => {
    console.log(user)
    users[user.id - 1] = user
    setUsers([...users])
  }

// редактирование пользователя
// поиск
// type email in input

  return (
    <div>
      <Header />
      <SortUsers
        selectSort={selectSort}
        sortUsers={sortUsers}
        defaultValue='Сортировка по'
        options={[{ value: 'first_name', name: 'По имени' }, { value: 'last_name', name: 'По фамилии' }]}

      />
      <main>
        <UsersList users={users} removeUser={removeUser} EditUser={EditUser} />
      </main>
      <aside>
        <AddUser users={users} onAddNewUser={onAddNewUser} />
      </aside>
    </div>
  )
}


export default App
