import React, { useState } from 'react'

const TestAdd = ({add, users, onRemoveUser}) => {
  users.map(u => console.log(u))

  const [newUser, setNewUser] = useState({name:'', username:''})
  const onAddUser = () => {
    const user = {
      ...newUser,
      id: users.length + 1
    }
    add(user)
    setNewUser({name:'', username:''})
  }
  return (
    <div>
      
      <input placeholder='first_name' value={newUser.name} onChange={e => setNewUser({...newUser, name: e.target.value})} />
      <input placeholder='last_name' value={newUser.username} onChange={e => setNewUser({...newUser, username: e.target.value})} />
      <button type='button' onClick={onAddUser}>Add</button>
      {users.map(u => <button key={u.id} type='button' onClick={() => onRemoveUser(u)}>Remove</button>)}
      
    </div>
  )
}

export default TestAdd
