import React, { useState } from 'react';

const AddUser = (props) => {

  const [addUser, setAddUser] = useState({
    name:'',
     descriptions: '',
     age: 0,
  })

//   }
  return (
    <form>
      <input placeholder="Имя" onChange={(e) => setAddUser({name: e.target.value})}/>
      <input placeholder="Описание" onChange={(e) => setAddUser({descriptions: e.target.value})} />
      <input placeholder="Возраст" onChange={(e) => setAddUser({age: e.target.value})} />
      <label htmlFor="isAgree" onChange={(e) => setAddUser({})}>Согласен</label>
      <input type="checkbox" id='isAgree' />
      <button type="button">Добавить</button>
    </form>

  )
}

export default AddUser
