import React from "react"
import * as ReactDOMClient from 'react-dom/client';

// В теге с id='app' - будет находиться первый передаваемый параметр в render, принимает только один тег

const inputClick = () => console.log('Click mouse on input')
const mouseOver = () => console.log('Mouse Over')
const text = 'TEXT'

class Header extends React.Component {
  render() {
    return <header>Моя шапка сайта</header>
  }

}

// const Header = () => {
//   return (
//     <header>Шапка сайта</header>
//   )
// }

function App() {
  return (
    <div>
      <Header />
      <h1>{text}</h1><input placeholder='Text...' onClick={inputClick} onMouseEnter={mouseOver}></input>
    </div>
  )
}

// const element = <div><h1>{text}</h1><input placeholder = 'Text...' onClick={inputClick} onMouseEnter={mouseOver}></input></div>


const root = ReactDOMClient.createRoot(document.getElementById("root"))

