import React from "react"

class Header extends React.Component {
  render() {
    return <header>{this.props.info}{this.props.title}</header>
  }
}

class App extends React.Component {
  text = 'TEXT'
  render() {
    return (
      <div>
        <Header title='Мой сайт' />
        <Header info='Информация о странице' />
        <h1>{this.text}</h1><input placeholder='Text...' onClick={this.inputClick} onMouseEnter={this.mouseOver}></input>
      </div>
    )
  }
  inputClick() {console.log('Click mouse on input')}
  mouseOver() {console.log('Mouse Over')}
}

export default App
