import React, { useState } from 'react'

const test = ({add, users}) => {
  const [newUser, setNewUser] = useState({first_name:'', last_name:''})
  const onAddUser = () => {
    const user = {
      ...newUser,
      id: users.length + 1
    }
    add(user)
    setNewUser({first_name:'', last_name:''})
  }
  return (
    <div>
      
      <input placeholder='first_name' value={newUser.first_name} onChange={e => setNewUser({...newUser, first_name: e.target.value})} />
      <input placeholder='last_name' value={newUser.last_name} onChange={e => setNewUser({...newUser, last_name: e.target.value})} />
      <button type='button' onClick={onAddUser}>Add</button>
    </div>
  )
}

export default test
