import React, { useState } from 'react'

const EditUser = (props) => {

  const [addNewUser, setAddNewUser] = useState({first_name: '', last_name:'', age:'',isAgree: ''})

  const onEditUser = () => {
    const newUser = {
      ...addNewUser,
       id: props.user.id
    }
    props.oneEditUser(newUser)
    setAddNewUser({first_name: '', last_name:'', age:'',isAgree: ''})

    // if(props.user){
    //   newUser.id = props.user.id
    // }

  }
  return (
    <form>
      <input type='text' placeholder='Имя' value={addNewUser.first_name} onChange={e => setAddNewUser({...addNewUser, first_name: e.target.value})}/>
      <input type='text' placeholder='О себе' value={addNewUser.last_name} onChange={e => setAddNewUser({...addNewUser, last_name: e.target.value})}/>
      <input type='text' placeholder='Возраст' value={addNewUser.age} onChange={e => setAddNewUser({...addNewUser, age: e.target.value})}/>

      <label htmlFor="isAgree" onChange={e => setAddNewUser({...addNewUser, isAgree: e.target.checked})}>Согласен</label>
      <input type="checkbox" id='isAgree' />
      <button type="button" onClick={onEditUser}>Изменить</button>
    </form>
  )
}

export default EditUser
