import React from "react"
import * as ReactDOMClient from 'react-dom/client';

// В теге с id='app' - будет находиться первый передаваемый параметр в render, принимает только один тег

// const inputClick = () => console.log('Click mouse on input')
// const mouseOver = () => console.log('Mouse Over')
// const text = 'TEXT'

class Header extends React.Component {
  render() {
    return <header>{this.props.info}{this.props.title}</header>
  }

}

// const Header = () => {
//   return (
//     <header>Шапка сайта</header>
//   )
// }

class App extends React.Component {
  text = 'TEXT'
  render() {
    return (
      <div>
        <Header title='Мой сайт' />
        <Header info='Информация о странице' />
        <h1>{this.text}</h1><input placeholder='Text...' onClick={this.inputClick} onMouseEnter={this.mouseOver}></input>
      </div>
    )
  }
  inputClick() {console.log('Click mouse on input')}
  mouseOver() {console.log('Mouse Over')}
}

