import User from "./User"

const Users = () => {
  const state = {
    users:[
    {
      id: 1,
      name: 'Sam Bolt',
      descriptions: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Quis, illum!',
    },
    {
      id: 2,
      name : 'Bob Tomhpson',
      descriptions: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Aliquam, officia aut!',
    },
  ]}

  if (state.users.length > 0){
    return(<div>
      {state.users.map((el)=> (
      <User user={el} key={el.id} />
      ))}

    </div>)
  }
  else{
    return(<div className="user">
      <h2>Нет пользователей</h2>
    </div>)
  }

}

export default Users
