    },
    {
      id: 2,
      name : 'Bob Tomhpson',
      descriptions: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Aliquam, officia aut!',
    },
  ]}

  if (users.length > 0){
    return(<div>
      {state.users.map((el)=> (<div className="user" key={el.id}>
        <h3>{el.name}</h3>
        <p>{el.descriptions}</p>
      </div>))}
    </div>)
  }
  else{
    return(<div className="user">
      <h2>Нет пользователей</h2>
    </div>)
  }

}

