import { useState } from 'react'
import { IoBackspaceOutline, IoPencilOutline } from 'react-icons/io5'
import AddUser from './AddUser'

const User = (props) => {
  const [form, setForm] = useState({editForm: false})
  return (
    <div className="user" >
      <IoBackspaceOutline className='delete-icon' onClick={() => props.removeUser(props.user)} />
      <IoPencilOutline className='edit-icon' onClick={() => setForm(!form.editForm)} />
        <h3>{props.user.first_name}</h3>
        <p>{props.user.last_name}</p>
        <p>{props.user.age}</p>
        {form.editForm && <AddUser />}
    </div>
  )
} 

export default User
