import React from "react"
import AddUser from "./components/AddUser"
import Header from "./components/Header"
import Users from "./components/Users"
import axios from 'axios'

const url = 'https://reqres.in/api/users?page=1'

const App = () => {

  axios.get(url).then((response) => {
    console.log(response.data.data)
  })

  const state = {
    users:[
    {
      id: 1,
      name: 'Sam Bolt',
      descriptions: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Quis, illum!',
      age: 22,
    },
    {
      id: 2,
      name : 'Bob Tomhpson',
      descriptions: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Aliquam, officia aut!',
      age: 33,
    },
    ],
    newUser: 
      {
        name: '',
        descriptions: '',
        age: 0,
      }
    
  }

  return (
    <div>
      <Header />
      <main>
        <Users users={state.users}  />
      </main>
      <aside>
        <AddUser />
      </aside>
    </div>
  )
}


export default App
