import React, { useState } from "react"
import AddUser from "./components/AddUser"
import Header from "./components/Header"
import Users from "./components/Users"
import axios from 'axios'
import SortUsers from "./components/SortUsers"

const url = 'https://reqres.in/api/users?page=1'

const App = () => {

const [users, setUsers] = useState([{id: 1, email:'sql@gmail.com', first_name: 'Sam', last_name: 'Hi! im Sam!', age: 40, isAgree: true}])

axios.get(url).then((response) => {
  setUsers({users: response.data.data})
})

  
  const onAddNewUser = (newUser) => {
    setUsers([...users, newUser])
 }

  const removeUser = (user) => {
    setUsers(users.filter(u => u.id !== user.id))
  }



  return (
    <div>
      <Header />
      {/* <SortUsers /> */}
      <main>
        <Users users={users} removeUser={removeUser} />
      </main>
      <aside>
        <AddUser onAddNewUser={onAddNewUser} />
      </aside>
    </div>
  )
}


export default App
